// Types of relationships:

// contains one
parent = {
	child: {}
}

// contains many
parent = {
	children: [ {}, {} ]
}

// finds one
parent = {}
child = { parentId: parent._id }

// has one
parent = { childId: child._id }
child = {}

// finds many
parent = {}
child1 = { parentId: parent._id }
child2 = { parentId: parent._id }

// has many
parent = { children: [ child1._id, child2._id ] }
child1 = {}
child2 = {}

// many to many (has many x2)
parent1 = { children: [ child1._id, child2._id ] }
parent2 = { children: [ child2._id ] }
child1 = { parents: [ parent1._id ] }
child2 = { parents: [ parent1._id, parent2._id ] }